//		技能部分开始

			 function funn(boxName,num,x,y,r,r1,fontFa){
           		 var c = document.getElementById(boxName),
            
                process = 0,  
                ctx = c.getContext('2d');  
                
				
            // 画灰色的圆  
            ctx.beginPath();  
            ctx.arc(50, 50, r, 0, Math.PI*2);  
            ctx.closePath();  
            ctx.fillStyle = '#F6F6F6';  
            ctx.fill();  
  
            function animate(){  
                requestAnimationFrame(function (){  
                    process = process + 1;  
                    drawCricle(ctx, process);  
                    if (process < num) { 
                    	animate(); 
                    }  
                });  
            }  
  
            function drawCricle(ctx, percent){  
                // 画进度环  
                ctx.beginPath();  
                ctx.moveTo(50, 50);    
                ctx.arc(50, 50, r, Math.PI * 1.5, Math.PI * (1.5 + 2 * percent / 100 ));  
                ctx.closePath();  
                ctx.fillStyle = '#FF9600';  
                ctx.fill();  
  
                // 画内填充圆  
                ctx.beginPath();  
                ctx.arc(50, 50, r1, 0, Math.PI * 2);  
                ctx.closePath();  
                ctx.fillStyle = '#fff';  
                ctx.fill();  
  
                // 填充文字  
                ctx.font = fontFa;   
                ctx.fillStyle = '#333';  
                ctx.textAlign = 'center';    
                ctx.textBaseline = 'middle';    
                ctx.moveTo(x, y);    
                ctx.fillText(process + '%', 50, 50);  
            }  
				animate(); 
  			 }
				
					
		
//		技能部分结束





